static void _iot_mqtt_chunk_destroy(iot_mqtt_packet_chunk_t *chunk)
{
	if (chunk && chunk->chunk_data) {
		iot_os_free(chunk->chunk_data);
	}

	if (chunk && chunk->expiry_time) {
		iot_os_timer_destroy(&chunk->expiry_time);
	}

	if (chunk) {
		iot_os_free(chunk);
	}
}

