unsigned int MQTTPacket_getPacketId(unsigned char *buf)
{
	int packet_type = (buf[0] & 0xf0) >> 4;
	int qos = (buf[0] & 0x06) >> 1;
	int rem_size = 0;
	unsigned int packet_id = 0;

	switch (packet_type) {
		case PUBLISH:
			if (qos == 0)
				break;
			buf++;
			buf += MQTTPacket_decodeBuf(buf, &rem_size);
			buf += readInt(&buf);
			packet_id = readInt(&buf);
			break;
		case PUBACK:
		case PUBREC:
		case PUBREL:
		case PUBCOMP:
		case SUBSCRIBE:
		case SUBACK:
		case UNSUBSCRIBE:
		case UNSUBACK:
			buf++;
			buf += MQTTPacket_decodeBuf(buf, &rem_size);
			packet_id += readInt(&buf);
			break;
	}

	return packet_id;
}

