iot_error_t iot_nv_get_cloud_prov_data(struct iot_cloud_prov_data* cloud_prov)
{
	HIT();
	IOT_WARN_CHECK(cloud_prov == NULL, IOT_ERROR_INVALID_ARGS, "Invalid args 'NULL'");
	/*
	 * Todo :
	 * IOT_NVD_CLOUD_PROV_STATUS
