	if (ret != IOT_ERROR_NONE) {
		IOT_ERROR("failed to init manager");
		return IOT_ERROR_NV_DATA_ERROR;
	}

	ret = iot_security_manager_get_certificate(security_context, cert_id, &cert_buf);
