		return IOT_ERROR_NV_DATA_ERROR;
	}

	*cert = (char *)cert_buf.p;
	*len = cert_buf.len;

	(void)iot_security_manager_deinit(security_context);
	(void)_iot_nv_io_storage_deinit(security_context);

	return IOT_ERROR_NONE;
}

