iot_error_t iot_bsp_fs_close(iot_bsp_fs_handle_t handle)
{
	int ret;
	if (handle.fd < 0) {
		return IOT_ERROR_FS_NO_FILE;
	}

	ret = close(handle.fd);
	if (ret == -1) {
		return IOT_ERROR_FS_CLOSE_FAIL;
	}

	return IOT_ERROR_NONE;
}

