291427.code:
static iot_error_t _iot_security_url_encode(char *buf, size_t buf_len)
{
	size_t i;

	if (!buf) {
		IOT_ERROR("buf is null");
		IOT_ERROR_DUMP_AND_RETURN(INVALID_ARGS, 0);
	}

	if (!buf_len) {
		IOT_ERROR("length is zero");
		IOT_ERROR_DUMP_AND_RETURN(INVALID_ARGS, 0);
	}

	for (i = 0; i < buf_len; i++) {
		switch (buf[i]) {
		case '+':
			buf[i] = '-';
			break;
		case '/':
			buf[i] = '_';
			break;
		default:
			break;
		}
	}

	return IOT_ERROR_NONE;
}

