ssize_t http_packet_send(HTTP_CONN_H *handle, char *tx_buffer, size_t tx_buffer_len)
{
	int len;

	if (handle == NULL || is_http_conn_handle_initialized(handle) == false) {
		return -1;
	}
	if (tx_buffer == NULL) {
		return -1;
	}

	len = send(handle->accept_sock, tx_buffer, tx_buffer_len, 0);
