        f = (unsigned long long)h1 + st->pad[1] + (f >> 32); h1 = (unsigned long)f;
        f = (unsigned long long)h2 + st->pad[2] + (f >> 32); h2 = (unsigned long)f;
        f = (unsigned long long)h3 + st->pad[3] + (f >> 32); h3 = (unsigned long)f;

        U32TO8(mac +  0, h0);
        U32TO8(mac +  4, h1);
        U32TO8(mac +  8, h2);
        U32TO8(mac + 12, h3);

